mapper.insert:
INSERT INTO cid (conversationId, firstMessage)
VALUES (#{conversationId}, #{conversationId});

INSERT INTO uidcid (uuid, conversationId)
VALUES (#{uuid}, #{conversationId});

mapper.select:
SELECT firstMessage
FROM cid
WHERE conversationid = #{conversationId}

mapper.update:
UPDATE cid
SET firstMessage = #{summary}
WHERE cid.conversationid = #{cid}
