mapper.update:
-- 更新userpw表中的uuid对应的用户的role为普通用户,再删除admin表中的uuid对应的用户
        UPDATE userpw
        SET role = 'USER'
        WHERE uuid = #{uuid};

        DELETE FROM admininfo
        WHERE uuid = #{uuid};

mapper.delete:
DELETE FROM cid
WHERE conversationId = #{conversationId}
  AND EXISTS (
    SELECT 1 FROM uidcid
    WHERE uuid = #{uuid}
      AND conversationId = #{conversationId}
);

DELETE FROM uidcid
WHERE uuid = #{uuid}
  AND conversationId = #{conversationId}
  AND NOT EXISTS (
    SELECT 1 FROM uidcid
    WHERE conversationId = #{conversationId}
);
