mapper.delete:
DELETE FROM cid
WHERE conversationId = #{conversationId}
  AND EXISTS (
    SELECT 1 FROM uidcid
    WHERE uuid = #{uuid}
      AND conversationId = #{conversationId}
);

DELETE FROM uidcid
WHERE uuid = #{uuid}
  AND conversationId = #{conversationId}
  AND NOT EXISTS (
    SELECT 1 FROM uidcid
    WHERE conversationId = #{conversationId}
);

mapper.select:
SELECT uuid, username, password, role
FROM userpw
WHERE uuid = #{uuid}
